INSERT INTO soi_escudero VALUES (NULL,8,'2016-03-31 19:17:14',128,2,'''''(B)oboto ako pabor (sa Anti-Political Dynasty Bill) para hindi masabi na ginamit ko ang puwesto ko para paboran ang aming mga sarili. Hindi ako makikilahok sa debate dahil baka sabihin nila hinubog ko ang batas para mapaboran kami. Pero pagdating ng botohan, lalahok ako at boboto pabor d''yan.''''','https://www.youtube.com/watch?v=XcLNddl-KSk','Yes','2016-03-31 19:17:14');
INSERT INTO soi_escudero VALUES (NULL,8,'2016-03-31 19:17:14',129,3,'''''If we do not pass a tax reform law now, we will be back to square one in the next Congress. Meanwhile, Filipinos, whose taxes pay our salaries, will continue to be burdened by one of the highest income tax rates in the world.''''','http://www.senate.gov.ph/press_release/2015/0930_escudero1.asp','Yes','2016-03-31 19:17:14');
