INSERT INTO soi_escudero VALUES (NULL,8,'2016-03-31 19:17:14',143,17,'''''(T)ayo na lang ang huling tatlong bansa na sa 10 taon ang elementary at high school. Sa buong mundo, 12 taon na ang elementary at high school. We are the last three countries in the world na 10 taon pa lamang. Napag-iiwanan at mapag-iiwanan tayo kapag hindi natin ginawa iyan. Ang tingin ng ilang mga estudyante at magulang, 12 taong elementary at high school, tapos apat na taong kolehiyo pa, so dinagdagan ng dalawang taong pasanin ang aming mga balikat. Binabago na rin po namin ang aming, ang ating mga batas.''''','http://chizescudero.com/index.php/component/k2/item/1400-pangasinan-marine-merchant-academy','Yes','2016-03-31 19:17:14');
