INSERT INTO soi_robredo VALUES (NULL,11,'2016-03-31 19:17:14',190,1,'''''We need to share power with the people... This is the heart and soul of good governance. This allows decisions at the policy making table to truly be responsive to what people need on the ground and not defined by the crosshairs of politics, economic status, or family name.'''' ','http://www.rappler.com/thought-leaders/69004-leni-robredo-real-deal-president','Yes','2016-03-31 19:17:14');
INSERT INTO soi_robredo VALUES (NULL,11,'2016-03-31 19:17:14',191,2,'''''What I am not in favor of is if those are being inherited by family members para hindi makuha ng iba. ''Yong sa akin, mas mabuti kapag hindi sa iisang pamilya ''yan, wala naman tayong monopoly that has good intentions. Mas mabuti sana kung lahat may equal opportunities to run for office.''''','http://www.gmanetwork.com/news/story/285430/news/nation/leni-robredo-okay-with-more-than-one-family-member-running-for','Yes','2016-03-31 19:17:14');
INSERT INTO soi_robredo VALUES (NULL,11,'2016-03-31 19:17:14',192,3,'''''The ordinary government employees and private sector employees who only depend on their salaries are those who are heavily taxed... those who earn more are those who will have to bear the burden.''''','http://www.manilatimes.net/leni-pnoy-clash-on-taxes/222664/','Yes','2016-03-31 19:17:14');
