INSERT INTO soi_robredo VALUES (NULL,11,'2016-03-31 19:17:14',209,20,'''''Ako ''yong posisyon ko kasi d''yan, dapat ''yong karapatan na binibigay kay President Arroyo available din sa lahat. Hindi p''wedeng parang may special treatment just because it is her. Kung mayroong mga karapatan na ibibigay sa kanya, dapat ''yong karapatan na ito, available din sa lahat.'''' <b>- Interview with Interaksyon, Feb. 17, 2016</b>',NULL,'No','2016-03-31 19:17:14');
INSERT INTO soi_robredo VALUES (NULL,11,'2016-03-31 19:17:14',210,21,'''''Ako kasi, ''yong assumption d''yan, may katiwalian. ''Yong sa akin, yung sagot ko diyan, lahat ng klaseng katiwalian dapat i-pursige ng gobyernong suyurin, hindi ''yong DAP as a concept pero lahat ng klase ng katiwalaan. Kailangan hindi hihinto ang gobyerno hangga''t hindi napaparusahan ''yong lahat ng nagkasala.'''' <b>- Interview with Interaksyon, Feb. 17, 2016</b>',NULL,'Yes','2016-03-31 19:17:14');
